                                            <script>
                                                $(document).ready(function() {


                                                    $('#phoneAuthModal').on('hidden.bs.modal', function() {
                                                        setCookie('jobsgo-tel-verify', 1, 1);
                                                    });

                                                    var phoneNumber;
                                                    var verificationId;

                                                    // Show the popup modal when the button is clicked
                                                    if (!getCookie('jobsgo-tel-verify')) {
                                                        //$("#phoneAuthModal").modal("show");
                                                    }

                                                    // Handle form submission
                                                    $("#phoneAuthForm").submit(function(event) {
                                                        event.preventDefault();
                                                        phoneNumber = $("#phoneNumber").val().replace(/^(84|0)/, '84').replace(/\D/g, '');

                                                        $("#phoneAuthModal").modal("hide");

                                                        $.post("/api/verify-zalo", {
                                                                phone: phoneNumber,
                                                            },
                                                            function(data) {
                                                                if (data == 1) {
                                                                    colorgbJGrowl('bg-warning', 'Vui lòng kiểm tra Zalo để lấy mã xác thực');
                                                                    $("#otpVerificationModal").modal("show");

                                                                } else {
                                                                    colorgbJGrowl('bg-warning', 'Không thể xác minh, vui lòng thử lại sau');
                                                                }

                                                            });
                                                    });

                                                    // Handle OTP verification
                                                    $("#otpVerificationForm").submit(function(event) {
                                                        event.preventDefault();
                                                        var code = $("#otpCode").val();
                                                        var phoneNumber = $("#phoneNumber").val().replace(/^(84|0)/, '84').replace(/\D/g, '');

                                                        $.post("/api/verify-zalo-otp", {
                                                                code: code,
                                                                phone: phoneNumber,
                                                            },
                                                            function(data) {
                                                                if (data == 1) {
                                                                    colorgbJGrowl('bg-success', 'Xác minh SĐT thành công');
                                                                    location.reload();

                                                                } else {
                                                                    colorgbJGrowl('bg-warning', 'Mã xác thực không đúng hoặc đã hết hạn');
                                                                }

                                                            });

                                                    });
                                                });
                                            </script>





