                                                function applyMobileStyles() {
                                                    const isMobile = window.innerWidth < 768;

                                                    if (isMobile) {
                                                        const navItems = document.querySelectorAll('.cd-breadcrumb li a');
                                                        const childItems = document.querySelectorAll('.cd-breadcrumb li a .nav-item');
                                                        const screenWidth = window.innerWidth;
                                                        const halfScreenWidth = screenWidth / 2;

                                                        const addMobileCSS = (element) => {
                                                            element.style.width = `${halfScreenWidth - 15}px`;
                                                            element.style.fontSize = `12px`;
                                                        };

                                                        navItems.forEach(addMobileCSS);

                                                        const lastItem = navItems[navItems.length - 1];
                                                        lastItem.style.width = `${halfScreenWidth}px`;

                                                        childItems.forEach((item, index) => {
                                                            if (index === 0) {
                                                                item.style.paddingLeft = '15px';
                                                            }
                                                        });

                                                        const styleElement = document.createElement('style');
                                                        styleElement.appendChild(document.createTextNode('.nav-item {margin-bottom: 3px !important; margin-top: 4px}'));

                                                        document.head.appendChild(styleElement);
                                                    }
                                                }

