                                                                $('.delete-cv').click(function(event) {
                                                                    let id = $(this).data('id');
                                                                    let r = confirm("Bạn có chắc chắn muốn xóa CV này?");
                                                                    if (r == true) {
                                                                        $.ajax({
                                                                                url: '/create-resume/delete-cv',
                                                                                type: 'POST',
                                                                                dataType: 'json',
                                                                                data: {
                                                                                    id: id
                                                                                },
                                                                            })
                                                                            .done(function(response) {
                                                                                if (response.status) {
                                                                                    location.reload();
                                                                                } else {
                                                                                    alert(response.msg);
                                                                                }
                                                                            })
                                                                            .fail(function() {})
                                                                            .always(function() {});
                                                                    }
                                                                });

