                                                                $('.cv-name-title').bind('DOMSubtreeModified', function() {
                                                                    let id = $(this).data('id');
                                                                    let name = $(this).text();
                                                                    $.ajax({
                                                                            url: '/create-resume/update-cv-name',
                                                                            type: 'POST',
                                                                            dataType: 'json',
