                                                        var i5 = $('#colorgbModal21 #eduinfoform-hightlight');
                                                        var i6 = $('#colorgbModal21 #current');
                                                        var id = $('#colorgbModal21 #id');

                                                        if (i1.val() == '') {
                                                            i1.focus();
                                                            colorgbJGrowl('bg-warning', 'Vui lòng nhập tên trường học');
                                                        } else if (i2.val() == '') {
                                                            colorgbJGrowl('bg-warning', 'Vui lòng nhập năm tốt nghiệp');
                                                        } else if (i3.val() == '') {
                                                            colorgbJGrowl('bg-warning', 'Vui lòng chọn bằng cấp');
                                                        } else if (i4.val() == '') {
                                                            i4.focus();
                                                            colorgbJGrowl('bg-warning', 'Vui lòng nhập chuyên ngành học');
                                                        } else if (i5.val() == '' || i5.val().length < 50) {
                                                            i5.focus();
                                                            colorgbJGrowl('bg-warning', 'Vui lòng mô tả quá trình học tập (tối thiểu 50 ký tự)');
                                                        } else {

                                                            var data = {
                                                                id: id.val(),
                                                                i1: i1.val(),
                                                                i2: i2.val(),
                                                                i3: i3.val(),
                                                                i4: i4.val(),
                                                                i5: i5.val(),
                                                                i6: i6.val(),
                                                            };
