                                                        var c = $('#colorgbModal21');

                                                        c.find('#id').val(t.data('id'));
                                                        c.find('#eduinfoform-school_name').val(t.data('i1'));
                                                        c.find('#eduinfoform-date_end').val(t.data('i2')).trigger('change');
                                                        c.find('#eduinfoform-degree_id').val(t.data('i3')).trigger('change');
                                                        c.find('#eduinfoform-specialization').val(t.data('i4'));
                                                        c.find('#eduinfoform-hightlight').val(t.data('i5'));

                                                        if (t.data('i6') == 1) {
                                                            c.find('#current').trigger('click');
                                                        }

                                                    });

                                                    $('.btn-add').click(function() {
                                                        var t = $(this);
                                                        $('#colorgbModal21 #id').val(t.data('id'));
                                                    });

                                                    $('.btn-del-ce').on('click.krajee', function(e, options) {
                                                        var t = $(this),
                                                            lib = window['krajeeDialog'];
                                                        var msg = 'Bạn có chắc muốn xóa quá trình học tập này?';

                                                        e.stopPropagation();
                                                        e.preventDefault();
                                                        lib.confirm(msg, function(result) {

                                                            if (!result) {
                                                                return;
                                                            }
                                                            t.attr('disabled', 'disabled');

                                                            $.ajax({
                                                                url: "/api/del-ce?id=" + t.data('id'),
                                                                success: function(result) {
                                                                    f1();
                                                                    colorgbJGrowl('bg-success', 'Xóa quá trình học tập thành công');
                                                                    updateReviewPanel();
                                                                }
                                                            });

                                                        });
                                                    });

                                                }

