                                                        url: "/api/get-cl",
                                                        success: function(result) {
                                                            $(".box-2 .colorgb-row").html(result);
                                                            f4();
                                                            $('.box-2 .row .btn-del-css').on('click.krajee', function(e, options) {
                                                                var t = $(this),
                                                                    lib = window['krajeeDialog'];
                                                                var msg = 'Bạn có chắc muốn xóa ngôn ngữ này?';

                                                                e.stopPropagation();
                                                                e.preventDefault();
                                                                lib.confirm(msg, function(result) {

                                                                    if (!result) {
                                                                        return;
                                                                    }
                                                                    t.attr('disabled', 'disabled');
                                                                    $('#colorgbModal24 .item-jcc a[data-id="' + t.data('iid') + '"]').parent().removeClass('hide');

