                                                                            f3();
                                                                            colorgbJGrowl('bg-success', 'Xóa kỹ năng mềm thành công');
                                                                        }
                                                                    });

                                                                });
                                                            });
                                                        }
                                                    });

                                                }

                                                function f4() {
                                                    $(".box-2 .row a").click(function() {
                                                        var id = $(this).data('id');
