                                                                $.ajax({
                                                                    url: "/api/set-cl?id=" + id + "&val=" + data.from,
                                                                    success: function(result) {
                                                                        f3();
                                                                        colorgbJGrowl('bg-success', 'Ngôn ngữ đã được cập nhật thành công');
                                                                    }
                                                                });

                                                            }
                                                        });

                                                    });
                                                }
