                                            <script>
                                                $(function() {


                                                    f60();

                                                    $('#colorgbModal61 .btn-primary').click(function() {
                                                        var i1 = $('#colorgbModal61 [name="cid[]"]:last');
                                                        var i2 = $('#colorgbModal61 [name="cexp[]"]:last');

                                                        if (i1.val() == '') {
                                                            i1.focus();
                                                            colorgbJGrowl('bg-warning', 'Vui lòng chọn kỹ năng chuyên môn');
                                                        } else if (i2.val() == '') {
                                                            colorgbJGrowl('bg-warning', 'Vui lòng chọn số năm kinh nghiệm');
                                                        } else {

                                                            var cids = $('#colorgbModal61 [name="cid[]"]').map(function() {
                                                                return $(this).val();
                                                            }).get().join(',');
                                                            var cexps = $('#colorgbModal61 [name="cexp[]"]').map(function() {
                                                                return $(this).val();
                                                            }).get().join(',');

                                                            $.ajax({
                                                                method: 'POST',
                                                                url: "/api/set-cjcs",
                                                                data: {
                                                                    i1: cids,
                                                                    i2: cexps,
                                                                },
                                                                success: function(result) {
                                                                    colorgbJGrowl('bg-success', 'Cập nhật dữ liệu thành công');
                                                                    $('#colorgbModal61').modal('hide');
                                                                    f60();
                                                                    updateReviewPanel();

                                                                }
                                                            });
                                                        }
                                                    });


                                                });

                                                function f60() {

                                                    $('.btn-add[data-target="#colorgbModal61"]').click(function() {
                                                        var c = $('#colorgbModal61');
                                                        $('.btn-clone-row-cjc').removeClass('hide');
                                                        c.find('[name="cid[]"]').val('').prop('disabled', false).trigger('change').select2({
                                                            placeholder: 'Tìm kiếm...',
                                                            ajax: {
                                                                url: '/api/job-categories', // Thay thế bằng URL của endpoint AJAX của bạn
                                                                dataType: 'json',
                                                                delay: 250, // Độ trễ trước khi gửi yêu cầu AJAX (nếu cần)
                                                                processResults: function(data) {
                                                                    // Xử lý dữ liệu trả về từ endpoint AJAX
                                                                    return {
                                                                        results: $.map(data, function(item) {
                                                                            return {
                                                                                id: item.job_category_id,
                                                                                text: item.job_category_name_vn
                                                                            };
                                                                        })
                                                                    };
                                                                },
                                                                cache: true // Bật lưu cache nếu bạn muốn
                                                            }
                                                        });
                                                        c.find('[name="cexp[]"]').val('').prop('disabled', false).trigger('change').select2();
                                                    });
                                                    $.ajax({
                                                        url: "/api/get-cjcs",
                                                        success: function(result) {
                                                            $(".box-cjcs").html(result);
                                                            f62();
                                                        }
                                                    });

                                                }

                                                function f62() {

                                                    $('.btn-update-cjcs').click(function() {
                                                        var t = $(this);
                                                        var c = $('#colorgbModal61');
                                                        $('.btn-clone-row-cjc').addClass('hide');
                                                        $('#colorgbModal61 .row-cjc select').select2('destroy');
                                                        var clonedDiv = $('#colorgbModal61 .row-cjc > .row:nth-child(1)').clone();
                                                        $("#colorgbModal61 .row-cjc").html(clonedDiv);
                                                        c.find('[name="cid[]"]:first').val(t.data('i1')).prop('disabled', true).trigger('change').select2({
                                                            placeholder: t.data('name'),
                                                            ajax: {
                                                                url: '/api/job-categories', // Thay thế bằng URL của endpoint AJAX của bạn
                                                                dataType: 'json',
                                                                delay: 250, // Độ trễ trước khi gửi yêu cầu AJAX (nếu cần)
                                                                processResults: function(data) {
                                                                    // Xử lý dữ liệu trả về từ endpoint AJAX
                                                                    return {
                                                                        results: $.map(data, function(item) {
