                                                                        })
                                                                    };
                                                                },
                                                                cache: true // Bật lưu cache nếu bạn muốn
                                                            }
                                                        });
                                                        c.find('[name="cexp[]"]:first').val(t.data('i2')).trigger('change').select2();

                                                    });


                                                    $('.btn-del-cjcs').on('click.krajee', function(e, options) {
                                                        var t = $(this),
                                                            lib = window['krajeeDialog'];
                                                        var msg = 'Bạn có chắc muốn xóa kỹ năng chuyên môn này?';

                                                        e.stopPropagation();
                                                        e.preventDefault();
                                                        lib.confirm(msg, function(result) {

                                                            if (!result) {
                                                                return;
                                                            }
                                                            t.attr('disabled', 'disabled');

